INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B1_I2', 'ingles-b2-U3', 2, 'multiple_choice', 'Choose the best option to complete the sentences about formal writing.', '{"prompt_es":"Choose the best option to complete the sentences about formal writing.","mastery_tag":"writing:b2-formal-writing","explanation":"Un informe formal debe tener un encabezado claro que guíe al lector sobre el contenido.","stimulus_en":"A formal report should have a clear ____ to guide the reader.","options":[{"id":"o1","text":"introduction"},{"id":"o2","text":"summary"},{"id":"o3","text":"conclusion"},{"id":"o4","text":"heading"}],"correct_answer":"o4","complexity":3,"concept_tags":["writing","formal","unit","grammar"]}')
