INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B1_I3', 'ingles-b2-U3', 3, 'multiple_choice', 'Choose the best option to complete the sentences about formal writing.', '{"prompt_es":"Choose the best option to complete the sentences about formal writing.","mastery_tag":"writing:b2-formal-writing","explanation":"Al escribir un correo electrónico formal, es esencial incluir un asunto adecuado para que el destinatario sepa de qué trata.","stimulus_en":"When writing a formal email, it is essential to include a proper ____.","options":[{"id":"o1","text":"subject"},{"id":"o2","text":"greeting"},{"id":"o3","text":"signature"},{"id":"o4","text":"paragraph"}],"correct_answer":"o1","complexity":3,"concept_tags":["writing","formal","unit","grammar"]}')
