INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B1_I4', 'ingles-b2-U3', 4, 'multiple_choice', 'Choose the best option to complete the sentences about formal writing.', '{"prompt_es":"Choose the best option to complete the sentences about formal writing.","mastery_tag":"writing:b2-formal-writing","explanation":"Un ensayo formal efectivo debe presentar un argumento bien estructurado que sustente el tema tratado.","stimulus_en":"An effective formal essay should present a well-structured ____.","options":[{"id":"o1","text":"argument"},{"id":"o2","text":"narrative"},{"id":"o3","text":"story"},{"id":"o4","text":"summary"}],"correct_answer":"o1","complexity":3,"concept_tags":["writing","formal","unit","grammar"]}')
