INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B1_I5', 'ingles-b2-U3', 5, 'multiple_choice', 'Choose the best option to complete the sentences about formal writing.', '{"prompt_es":"Choose the best option to complete the sentences about formal writing.","mastery_tag":"writing:b2-formal-writing","explanation":"En la escritura formal, usar un vocabulario complejo puede aumentar la claridad de tu documento, siempre y cuando sea apropiado.","stimulus_en":"In formal writing, using complex vocabulary can enhance the ____ of your document.","options":[{"id":"o1","text":"clarity"},{"id":"o2","text":"length"},{"id":"o3","text":"confusion"},{"id":"o4","text":"simplicity"}],"correct_answer":"o1","complexity":3,"concept_tags":["writing","formal","unit","grammar"]}')
