VALUES ('B2_U3_B2_I1', 'ingles-b2-U3', 6, 'multiple_choice', 'Choose the correct answer for each question about expressing wishes and regrets.', '{"prompt_es":"Choose the correct answer for each question about expressing wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"En esta oración, se utiliza ''had'' porque se expresa un deseo sobre algo que no es real en el pasado.","stimulus_en":"I wish I ___ more time to finish the project.","options":[{"id":"o1","text":"had"},{"id":"o2","text":"have"},{"id":"o3","text":"was"},{"id":"o4","text":"will"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
