INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B2_I2', 'ingles-b2-U3', 7, 'multiple_choice', 'Choose the correct answer for each question about expressing wishes and regrets.', '{"prompt_es":"Choose the correct answer for each question about expressing wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Se utiliza ''were'' en frases hipotéticas, aunque el sujeto sea singular.","stimulus_en":"If only I ___ more prepared for the presentation.","options":[{"id":"o1","text":"am"},{"id":"o2","text":"was"},{"id":"o3","text":"were"},{"id":"o4","text":"have been"}],"correct_answer":"o3","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
