INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B2_I3', 'ingles-b2-U3', 8, 'multiple_choice', 'Choose the correct answer for each question about expressing wishes and regrets.', '{"prompt_es":"Choose the correct answer for each question about expressing wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Se usa el pasado simple ''missed'' para expresar un arrepentimiento sobre un evento que ya sucedió.","stimulus_en":"I regret that I ___ my chance to travel abroad last year.","options":[{"id":"o1","text":"missed"},{"id":"o2","text":"was missing"},{"id":"o3","text":"have missed"},{"id":"o4","text":"miss"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
