INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B2_I4', 'ingles-b2-U3', 9, 'multiple_choice', 'Choose the correct answer for each question about expressing wishes and regrets.', '{"prompt_es":"Choose the correct answer for each question about expressing wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Se utiliza ''had attended'' para expresar un deseo sobre algo que no sucedió en el pasado.","stimulus_en":"I wish I ___ the meeting yesterday.","options":[{"id":"o1","text":"attend"},{"id":"o2","text":"attended"},{"id":"o3","text":"was attending"},{"id":"o4","text":"have attended"}],"correct_answer":"had attended","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
