INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B2_I5', 'ingles-b2-U3', 10, 'multiple_choice', 'Choose the correct answer for each question about expressing wishes and regrets.', '{"prompt_es":"Choose the correct answer for each question about expressing wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Se usa el pasado perfecto ''had submitted'' para expresar arrepentimiento sobre una acción no realizada en el pasado.","stimulus_en":"If only I ___ my homework on time.","options":[{"id":"o1","text":"had submitted"},{"id":"o2","text":"submitted"},{"id":"o3","text":"was submitting"},{"id":"o4","text":"have submitted"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
