INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B3_I1', 'ingles-b2-U3', 11, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"En inglés, la inversión se usa a menudo con ''rarely'' para enfatizar la frase.","stimulus_en":"Rarely ___ such a talented musician.","options":[{"id":"o1","text":"have I seen"},{"id":"o2","text":"I have seen"},{"id":"o3","text":"saw I"},{"id":"o4","text":"I saw"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
