INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U3_B3_I2', 'ingles-b2-U3', 12, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La frase se forma usando ''do'' para la inversión con ''seldom''.","stimulus_en":"Seldom ___ individuals possess such unique skills.","options":[{"id":"o1","text":"do"},{"id":"o2","text":"does"},{"id":"o3","text":"have"},{"id":"o4","text":"are"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
