VALUES ('B2_U3_B3_I3', 'ingles-b2-U3', 13, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"Usamos ''had'' en la inversión con ''never'' para referirnos a experiencias pasadas.","stimulus_en":"Never ___ I witness such an incredible performance before.","options":[{"id":"o1","text":"had"},{"id":"o2","text":"have"},{"id":"o3","text":"did"},{"id":"o4","text":"has"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
