VALUES ('B2_U3_B3_I4', 'ingles-b2-U3', 14, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión con ''not until'' requiere ''did'' para enfatizar la situación.","stimulus_en":"Not until the meeting ended ___ we understand the full impact of the decision.","options":[{"id":"o1","text":"did"},{"id":"o2","text":"do"},{"id":"o3","text":"was"},{"id":"o4","text":"have"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
