INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U4_B1_I1', 'ingles-b2-U4', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''STUDY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''STUDY'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Aquí debes empezar la oración con la cláusula del pasado, manteniendo la idea original de la condición y el resultado.","stimulus_en":"If I had studied harder, I would be a doctor now.\n\nHad I studied... ...","correct_answer":"harder, I would be a doctor now.","example":"STUDY","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U4_B1_I2', 'ingles-b2-U4', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''KNOW'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''KNOW'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"La transformación requiere el uso del pasado en la cláusula condicional, para reflejar el significado original.","stimulus_en":"If she had known the truth, she would act differently.\n\nHad she known... ...","correct_answer":"the truth, she would act differently.","example":"KNOW","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U4_B1_I3', 'ingles-b2-U4', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''ARRIVE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''ARRIVE'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Debes iniciar la oración con la hipótesis del pasado y conectar adecuadamente con el resultado que se menciona.","stimulus_en":"If we had arrived earlier, we would see the start of the show.\n\nHad we arrived... ...","correct_answer":"earlier, we would see the start of the show.","example":"ARRIVE","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U4_B1_I4', 'ingles-b2-U4', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''TAKE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''TAKE'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Necesitas transformar la cláusula condicional manteniendo la relación de causa y efecto entre el pasado y el presente.","stimulus_en":"If he had taken the job, he would be living in France now.\n\nHad he taken... ...","correct_answer":"the job, he would be living in France now.","example":"TAKE","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
