INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U4_B1_I5', 'ingles-b2-U4', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''KNOW'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''KNOW'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"La transformación implica usar la forma correcta del pasado para mostrar lo que podría haber sido.","stimulus_en":"If they had known about the meeting, they would join us.\n\nHad they known... ...","correct_answer":"about the meeting, they would join us.","example":"KNOW","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
