VALUES ('B2_U4_B2_I5', 'ingles-b2-U4', 10, 'multiple_choice', 'Which factor can negatively impact one''s lifestyle?', '{"prompt_es":"Which factor can negatively impact one''s lifestyle?","mastery_tag":"vocabulary:b2-lifestyle","explanation":"El estrés crónico puede tener efectos perjudiciales en la salud y el bienestar general.","stimulus_en":"","options":[{"id":"o1","text":"Positive relationships"},{"id":"o2","text":"Healthy diet"},{"id":"o3","text":"Chronic stress"},{"id":"o4","text":"Physical activity"}],"correct_answer":"o3","complexity":3,"concept_tags":["vocabulary","lifestyle","unit","grammar"]}')
