VALUES ('B2_U5_B1_I1', 'ingles-b2-U5', 1, 'multiple_choice', 'What is the primary purpose of journalism?', '{"prompt_es":"What is the primary purpose of journalism?","mastery_tag":"vocabulary:b2-media","explanation":"El objetivo principal del periodismo es informar al público sobre eventos y temas de interés.","stimulus_en":"","options":[{"id":"o1","text":"To entertain"},{"id":"o2","text":"To inform"},{"id":"o3","text":"To advertise"},{"id":"o4","text":"To promote"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","media","unit","grammar"]}')
