INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U5_B1_I2', 'ingles-b2-U5', 2, 'multiple_choice', 'Which term refers to news presented through audio and visual formats?', '{"prompt_es":"Which term refers to news presented through audio and visual formats?","mastery_tag":"vocabulary:b2-media","explanation":"El término ''broadcasting'' se refiere a la transmisión de noticias a través de formatos audiovisuales como la televisión y la radio.","stimulus_en":"","options":[{"id":"o1","text":"Written media"},{"id":"o2","text":"Broadcasting"},{"id":"o3","text":"Digital media"},{"id":"o4","text":"Print media"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","media","unit","grammar"]}')
