INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U5_B1_I3', 'ingles-b2-U5', 3, 'multiple_choice', 'What does ''digital media'' include?', '{"prompt_es":"What does ''digital media'' include?","mastery_tag":"vocabulary:b2-media","explanation":"Los ''medios digitales'' incluyen cualquier forma de comunicación que se presenta de manera digital, como periódicos en línea y redes sociales.","stimulus_en":"","options":[{"id":"o1","text":"Newspapers"},{"id":"o2","text":"Television"},{"id":"o3","text":"Social media"},{"id":"o4","text":"All of the above"}],"correct_answer":"o4","complexity":3,"concept_tags":["vocabulary","media","unit","grammar"]}')
