INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U5_B1_I4', 'ingles-b2-U5', 4, 'multiple_choice', 'What is a key challenge in digital journalism?', '{"prompt_es":"What is a key challenge in digital journalism?","mastery_tag":"vocabulary:b2-media","explanation":"Un desafío clave en el periodismo digital es mantener la calidad de la información en medio de la rápida difusión de noticias.","stimulus_en":"","options":[{"id":"o1","text":"Lack of audience"},{"id":"o2","text":"Speed of news delivery"},{"id":"o3","text":"Quality of information"},{"id":"o4","text":"Cost of production"}],"correct_answer":"o3","complexity":3,"concept_tags":["vocabulary","media","unit","grammar"]}')
