INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U5_B1_I5', 'ingles-b2-U5', 5, 'multiple_choice', 'What role do journalists play in society?', '{"prompt_es":"What role do journalists play in society?","mastery_tag":"vocabulary:b2-media","explanation":"Los periodistas tienen el papel crucial de proporcionar información objetiva y precisa a la sociedad.","stimulus_en":"","options":[{"id":"o1","text":"To entertain audiences"},{"id":"o2","text":"To influence politics"},{"id":"o3","text":"To provide information"},{"id":"o4","text":"To promote brands"}],"correct_answer":"o3","complexity":3,"concept_tags":["vocabulary","media","unit","grammar"]}')
