INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U8_B1_I1', 'ingles-b2-U8', 1, 'fill_blanks', 'Complete the sentences by filling in the blanks with appropriate words.', '{"prompt_es":"Complete the sentences by filling in the blanks with appropriate words.","mastery_tag":"writing:b2-formal-writing","explanation":"Un saludo adecuado es importante en una carta formal para establecer un tono profesional.","stimulus_en":"In today''s business environment, effective communication is essential. Writing formal letters and reports is a key skill, as it helps convey information clearly and professionally. Below are some sentences where you need to fill in the missing words.\n\nA formal letter usually begins with a proper ___.","correct_answer":"greeting","complexity":3,"concept_tags":["writing","formal","unit"]}')
