VALUES ('B2_U9_B1_I5', 'ingles-b2-U9', 5, 'multiple_choice', 'Which practice can enhance well-being?', '{"prompt_es":"Which practice can enhance well-being?","mastery_tag":"vocabulary:b2-lifestyle","explanation":"Mantener conexiones sociales es vital para la salud emocional y el bienestar general.","stimulus_en":"","options":[{"id":"o1","text":"Neglecting self-care"},{"id":"o2","text":"Maintaining social connections"},{"id":"o3","text":"Overworking"},{"id":"o4","text":"Avoiding hobbies"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","lifestyle","unit","grammar"]}')
