VALUES ('B2_U10_B1_I3', 'ingles-b2-U10', 3, 'multiple_choice', 'What should a speaker do to engage the audience?', '{"prompt_es":"What should a speaker do to engage the audience?","mastery_tag":"listening:b2-lectures","explanation":"Hacer preguntas a la audiencia mantiene su interés y les invita a participar en la charla.","stimulus_en":"","options":[{"id":"o1","text":"Speak monotonously"},{"id":"o2","text":"Ask questions"},{"id":"o3","text":"Ignore feedback"},{"id":"o4","text":"Use jargon"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
