VALUES ('B2_U10_B1_I4', 'ingles-b2-U10', 4, 'multiple_choice', 'Which of the following is a common evaluation method for presentations?', '{"prompt_es":"Which of the following is a common evaluation method for presentations?","mastery_tag":"listening:b2-lectures","explanation":"La revisión entre compañeros permite a los estudiantes recibir comentarios útiles sobre su desempeño en presentaciones.","stimulus_en":"","options":[{"id":"o1","text":"Peer review"},{"id":"o2","text":"Group fun"},{"id":"o3","text":"Individual study"},{"id":"o4","text":"Silent attention"}],"correct_answer":"o1","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
