VALUES ('B2_U10_B1_I5', 'ingles-b2-U10', 5, 'multiple_choice', 'What is an effective way to conclude a lecture?', '{"prompt_es":"What is an effective way to conclude a lecture?","mastery_tag":"listening:b2-lectures","explanation":"Resumir los puntos clave ayuda a los estudiantes a recordar la información importante presentada durante la conferencia.","stimulus_en":"","options":[{"id":"o1","text":"Summarize key points"},{"id":"o2","text":"Introduce new topics"},{"id":"o3","text":"Thank the audience"},{"id":"o4","text":"Stay silent"}],"correct_answer":"o1","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
