INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B2_I1', 'ingles-b2-U10', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''must have'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''must have'':","mastery_tag":"grammar:b2-perfect-modals","explanation":"Aquí se utiliza ''must have'' para expresar una obligación pasada que es muy probable que se haya cumplido.","stimulus_en":"It was necessary for him to attend the meeting. (must)\n\nHe must have ...","correct_answer":"attended the meeting.","example":"must have","complexity":3,"concept_tags":["grammar","perfect","modals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B2_I2', 'ingles-b2-U10', 7, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''should have'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''should have'':","mastery_tag":"grammar:b2-perfect-modals","explanation":"Aquí ''should have'' indica que era una buena idea hacer algo en el pasado.","stimulus_en":"It would have been a good idea to ask for help. (should)\n\nYou should have ...","correct_answer":"asked for help.","example":"should have","complexity":3,"concept_tags":["grammar","perfect","modals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B2_I3', 'ingles-b2-U10', 8, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''could have'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''could have'':","mastery_tag":"grammar:b2-perfect-modals","explanation":"Se utiliza ''could have'' para indicar una posibilidad en el pasado que no se cumplió.","stimulus_en":"It was possible for her to finish the project early. (could)\n\nShe could have ...","correct_answer":"finished the project early.","example":"could have","complexity":3,"concept_tags":["grammar","perfect","modals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B2_I4', 'ingles-b2-U10', 9, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''should have'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''should have'':","mastery_tag":"grammar:b2-perfect-modals","explanation":"Aquí se expresa un arrepentimiento sobre algo que no se hizo en el pasado.","stimulus_en":"I regret not studying more for the exam. (should)\n\nI should have ...","correct_answer":"studied more for the exam.","example":"should have","complexity":3,"concept_tags":["grammar","perfect","modals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B2_I5', 'ingles-b2-U10', 10, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''must have'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''must have'':","mastery_tag":"grammar:b2-perfect-modals","explanation":"Usamos ''must have'' para expresar una fuerte suposición sobre una acción pasada.","stimulus_en":"It''s very likely that he arrived late. (must)\n\nHe must have ...","correct_answer":"arrived late.","example":"must have","complexity":3,"concept_tags":["grammar","perfect","modals","unit"]}')
