INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B3_I1', 'ingles-b2-U10', 11, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''HARD'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''HARD'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"En esta oración transformamos la parte condicional para reflejar un hecho en el pasado que tiene consecuencias en el presente.","stimulus_en":"If he had studied harder, he would be successful now.\n\nIf he had studied harder, he would have... ...","correct_answer":"been successful now.","example":"HARD","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B3_I2', 'ingles-b2-U10', 12, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''LONELY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''LONELY'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Se utiliza la estructura condicional mixta para expresar cómo una acción pasada podría haber cambiado su estado actual.","stimulus_en":"She didn’t go to the party. Now she feels lonely.\n\nIf she had gone to the party, she wouldn''t feel... ...","correct_answer":"lonely now.","example":"LONELY","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B3_I3', 'ingles-b2-U10', 13, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''LATE'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''LATE'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"La transformación de esta oración muestra cómo una decisión pasada afecta el presente.","stimulus_en":"They missed the bus. Now they are late.\n\nIf they hadn''t missed the bus, they wouldn''t be... ...","correct_answer":"late now.","example":"LATE","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B3_I4', 'ingles-b2-U10', 14, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''HEALTHY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''HEALTHY'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Utilizamos esta versión para conectar acciones hipotéticas con resultados presentes.","stimulus_en":"He doesn''t exercise. He is not healthy.\n\nIf he exercised, he would be... ...","correct_answer":"healthy now.","example":"HEALTHY","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U10_B3_I5', 'ingles-b2-U10', 15, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''EXPERIENCES'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''EXPERIENCES'':","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Se usa el pasado perfecto para expresar cómo una acción pasada influyó en su presente.","stimulus_en":"She studied abroad. She has great experiences.\n\nIf she hadn''t studied abroad, she wouldn''t have had... ...","correct_answer":"such great experiences.","example":"EXPERIENCES","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
