VALUES ('B2_U16_B1_I4', 'ingles-b2-U16', 4, 'multiple_choice', 'What is a common structure for a review?', '{"prompt_es":"What is a common structure for a review?","mastery_tag":"writing:b2-reviews-articles","explanation":"Una reseña comúnmente sigue la estructura de introducción, cuerpo y conclusión para presentar información de manera organizada.","stimulus_en":"","options":[{"id":"o1","text":"Introduction, body, conclusion"},{"id":"o2","text":"Title, abstract, references"},{"id":"o3","text":"Introduction, evidence, discussion"},{"id":"o4","text":"Abstract, body, conclusions"}],"correct_answer":"o1","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
