VALUES ('B2_U16_B1_I5', 'ingles-b2-U16', 5, 'multiple_choice', 'Which aspect should be considered when evaluating a film in a review?', '{"prompt_es":"Which aspect should be considered when evaluating a film in a review?","mastery_tag":"writing:b2-reviews-articles","explanation":"La cinematografía es un aspecto clave a considerar al evaluar una película, ya que afecta la experiencia visual del espectador.","stimulus_en":"","options":[{"id":"o1","text":"Box office earnings"},{"id":"o2","text":"Director''s biography"},{"id":"o3","text":"Cinematography"},{"id":"o4","text":"Audience demographics"}],"correct_answer":"o3","complexity":3,"concept_tags":["writing","reviews","articles","unit","grammar"]}')
