INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U16_B2_I1', 'ingles-b2-U16', 6, 'fill_blanks', 'Complete the sentences with the correct words or phrases to express wishes and regrets.', '{"prompt_es":"Complete the sentences with the correct words or phrases to express wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Aquí estamos expresando un deseo que no se puede cumplir, ya que el examen ya pasó.","stimulus_en":"Fill in the blanks with appropriate expressions of wish or regret.\n\nI really ___ I had studied harder for that exam.","correct_answer":"wish","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
