INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U16_B2_I2', 'ingles-b2-U16', 7, 'fill_blanks', 'Complete the sentences with the correct words or phrases to express wishes and regrets.', '{"prompt_es":"Complete the sentences with the correct words or phrases to express wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Se utiliza ''had'' para expresar un deseo sobre una situación pasada que no se puede cambiar.","stimulus_en":"Fill in the blanks with appropriate expressions of wish or regret.\n\nIf only I ___ more time to prepare for the presentation.","correct_answer":"had","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
