INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U16_B2_I5', 'ingles-b2-U16', 10, 'fill_blanks', 'Complete the sentences with the correct words or phrases to express wishes and regrets.', '{"prompt_es":"Complete the sentences with the correct words or phrases to express wishes and regrets.","mastery_tag":"grammar:b2-wish-regret","explanation":"Este es otro ejemplo de un deseo sobre una decisión pasada que se lamenta.","stimulus_en":"Fill in the blanks with appropriate expressions of wish or regret.\n\nI ___ I had taken that job offer last year.","correct_answer":"wish","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
