INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B1_I1', 'ingles-b2-U17', 1, 'fill_blanks', 'Fill in the blanks using appropriate words to complete the sentences with correct inversion.', '{"prompt_es":"Fill in the blanks using appropriate words to complete the sentences with correct inversion.","mastery_tag":"grammar:b2-inversion","explanation":"El uso de ''rarely'' requiere inversión, por lo que se invierte ''I have seen''.","stimulus_en":"In the following sentences, fill in the gaps appropriately, employing the rules of inversion where needed.\n\n___ I have seen such beautiful art before.","correct_answer":"Rarely","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B1_I2', 'ingles-b2-U17', 2, 'fill_blanks', 'Fill in the blanks using appropriate words to complete the sentences with correct inversion.', '{"prompt_es":"Fill in the blanks using appropriate words to complete the sentences with correct inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se usa aquí por el uso de ''not only'' al principio de la oración.","stimulus_en":"In the following sentences, fill in the gaps appropriately, employing the rules of inversion where needed.\n\n___ did she finish her work on time, but she also helped others.","correct_answer":"Not only","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B1_I3', 'ingles-b2-U17', 3, 'fill_blanks', 'Fill in the blanks using appropriate words to complete the sentences with correct inversion.', '{"prompt_es":"Fill in the blanks using appropriate words to complete the sentences with correct inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se forma con ''hardly'' para expresar que algo ocurre con dificultad.","stimulus_en":"In the following sentences, fill in the gaps appropriately, employing the rules of inversion where needed.\n\n___ did the movie start, but the audience was already captivated.","correct_answer":"Hardly","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B1_I4', 'ingles-b2-U17', 4, 'fill_blanks', 'Fill in the blanks using appropriate words to complete the sentences with correct inversion.', '{"prompt_es":"Fill in the blanks using appropriate words to complete the sentences with correct inversion.","mastery_tag":"grammar:b2-inversion","explanation":"''No sooner'' indica que algo ocurrió casi inmediatamente después, requiriendo inversión.","stimulus_en":"In the following sentences, fill in the gaps appropriately, employing the rules of inversion where needed.\n\n___ had I left the house when it started to rain.","correct_answer":"No sooner","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
