INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B1_I5', 'ingles-b2-U17', 5, 'fill_blanks', 'Fill in the blanks using appropriate words to complete the sentences with correct inversion.', '{"prompt_es":"Fill in the blanks using appropriate words to complete the sentences with correct inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La palabra ''seldom'' introduce una inversión, destacando la rareza de la situación.","stimulus_en":"In the following sentences, fill in the gaps appropriately, employing the rules of inversion where needed.\n\n___ can you find someone as dedicated as her.","correct_answer":"Seldom","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
