INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B2_I1', 'ingles-b2-U17', 6, 'multiple_choice', 'Choose the correct answer for each of the following sentences that use mixed conditionals.', '{"prompt_es":"Choose the correct answer for each of the following sentences that use mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"En esta oración, el tiempo pasado ''had attended'' sugiere que ella no asistió a la reunión. El resultado en el presente es ''would be'', que indica que ahora estaría mejor informada.","stimulus_en":"If she had attended the meeting, she ___ better informed about the project now.","options":[{"id":"o1","text":"is"},{"id":"o2","text":"was"},{"id":"o3","text":"would be"},{"id":"o4","text":"has been"}],"correct_answer":"o3","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B2_I2', 'ingles-b2-U17', 7, 'multiple_choice', 'Choose the correct answer for each of the following sentences that use mixed conditionals.', '{"prompt_es":"Choose the correct answer for each of the following sentences that use mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"El uso de ''had'' es correcto aquí porque implica una situación pasada que afecta el resultado presente. Se forma así un condicional mixto.","stimulus_en":"If I ___ more time last week, I would have completed the report by now.","options":[{"id":"o1","text":"had"},{"id":"o2","text":"have"},{"id":"o3","text":"was"},{"id":"o4","text":"would have"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B2_I3', 'ingles-b2-U17', 8, 'multiple_choice', 'Choose the correct answer for each of the following sentences that use mixed conditionals.', '{"prompt_es":"Choose the correct answer for each of the following sentences that use mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"En esta oración, ''were'' se usa en la parte del condicional presente y ''would have gotten'' se usa para indicar que no recibió la promoción en el pasado debido a su falta de experiencia.","stimulus_en":"If he were more experienced, he ___ the promotion last year.","options":[{"id":"o1","text":"got"},{"id":"o2","text":"would get"},{"id":"o3","text":"would have gotten"},{"id":"o4","text":"had gotten"}],"correct_answer":"o3","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B2_I4', 'ingles-b2-U17', 9, 'multiple_choice', 'Choose the correct answer for each of the following sentences that use mixed conditionals.', '{"prompt_es":"Choose the correct answer for each of the following sentences that use mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Aquí, ''had known'' sugiere que no estaban al tanto de la fecha límite en el pasado, y ''would have made'' se utiliza para expresar una acción que no ocurrió.","stimulus_en":"If they had known about the deadline, they ___ more effort to meet it.","options":[{"id":"o1","text":"would have made"},{"id":"o2","text":"would make"},{"id":"o3","text":"had made"},{"id":"o4","text":"make"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U17_B2_I5', 'ingles-b2-U17', 10, 'multiple_choice', 'Choose the correct answer for each of the following sentences that use mixed conditionals.', '{"prompt_es":"Choose the correct answer for each of the following sentences that use mixed conditionals.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"El uso de ''were'' muestra una condición presente no cumplida, y ''would have finished'' describe un resultado pasado que no se realizó.","stimulus_en":"If our team were stronger, we ___ the last project last quarter.","options":[{"id":"o1","text":"would have finished"},{"id":"o2","text":"would finish"},{"id":"o3","text":"finish"},{"id":"o4","text":"finished"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
