VALUES ('B2_U18_B2_I3', 'ingles-b2-U18', 4, 'multiple_choice', 'What is one of the main roles of a government in society?', '{"prompt_es":"What is one of the main roles of a government in society?","mastery_tag":"vocabulary:b2-society","explanation":"El gobierno regula diversas áreas de la sociedad para mantener el orden y garantizar el bienestar.","stimulus_en":"","options":[{"id":"o1","text":"To entertain"},{"id":"o2","text":"To regulate"},{"id":"o3","text":"To play"},{"id":"o4","text":"To write"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
