INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U18_B2_I4', 'ingles-b2-U18', 5, 'multiple_choice', 'What issue is often discussed in terms of equality?', '{"prompt_es":"What issue is often discussed in terms of equality?","mastery_tag":"vocabulary:b2-society","explanation":"La educación se discute a menudo en términos de igualdad, ya que el acceso equitativo es fundamental para el progreso social.","stimulus_en":"","options":[{"id":"o1","text":"Education"},{"id":"o2","text":"Fashion"},{"id":"o3","text":"Travel"},{"id":"o4","text":"Technology"}],"correct_answer":"o1","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
