INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U18_B2_I5', 'ingles-b2-U18', 6, 'multiple_choice', 'Which term refers to the way citizens participate in their government?', '{"prompt_es":"Which term refers to the way citizens participate in their government?","mastery_tag":"vocabulary:b2-society","explanation":"El compromiso cívico se refiere a las formas en que los ciudadanos participan en su gobierno y sociedad.","stimulus_en":"","options":[{"id":"o1","text":"Citizenship"},{"id":"o2","text":"Activism"},{"id":"o3","text":"Civil engagement"},{"id":"o4","text":"Isolation"}],"correct_answer":"o3","complexity":3,"concept_tags":["vocabulary","society","unit","grammar"]}')
