INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B1_I1', 'ingles-b2-U21', 1, 'fill_blanks', 'Complete the sentences with the correct words related to debate and discussion.', '{"prompt_es":"Complete the sentences with the correct words related to debate and discussion.","mastery_tag":"speaking:b2-debate","main_text":"In a debate, it is important to ___ your point of view clearly and confidently.","correct_answer":["express","challenge","arguments","respond","respectful"],"explanation":"(1) Es importante ser claro y seguro al expresar tu punto de vista en un debate.\n(2) Los participantes desafían los argumentos de los demás para resaltar las debilidades.\n(3) Para persuadir a la audiencia, debes proporcionar argumentos sólidos y evidencia.\n(4) Un buen debatiente sabe cómo escuchar y responder a los contraargumentos de manera efectiva.\n(5) Es esencial mantener el respeto durante un debate para conservar el profesionalismo.","complexity":3,"concept_tags":["speaking","debate","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B2_I1', 'ingles-b2-U21', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''RARELY'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''RARELY'':","mastery_tag":"grammar:b2-inversion","explanation":"En inglés, cuando usamos ''rarely'' al inicio de una oración, se debe invertir el sujeto y el verbo auxiliar.","stimulus_en":"I have rarely seen such a beautiful sunset.\n\nRarely have I seen ...","correct_answer":"such a beautiful sunset.","example":"RARELY","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B2_I2', 'ingles-b2-U21', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''SELDOM'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''SELDOM'':","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se usa después de ''seldom'' para enfatizar la frecuencia baja de la acción.","stimulus_en":"I seldom go to parties.\n\nSeldom do I go ...","correct_answer":"to parties.","example":"SELDOM","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B2_I3', 'ingles-b2-U21', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''NEVER'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''NEVER'':","mastery_tag":"grammar:b2-inversion","explanation":"Se usa la inversión después de ''never'' para destacar la negación de la acción.","stimulus_en":"They have never visited Paris.\n\nNever have they visited ...","correct_answer":"Paris.","example":"NEVER","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B2_I4', 'ingles-b2-U21', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''SELDOM'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''SELDOM'':","mastery_tag":"grammar:b2-inversion","explanation":"Utilizamos ''seldom'' para enfatizar la baja frecuencia y requerimos inversión del sujeto y el auxiliar.","stimulus_en":"He has seldom been late.\n\nSeldom has he been ...","correct_answer":"late.","example":"SELDOM","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B2_I5', 'ingles-b2-U21', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''NEVER'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''NEVER'':","mastery_tag":"grammar:b2-inversion","explanation":"Al iniciar una oración con ''never'', se usa la inversión para hacer énfasis en la negación.","stimulus_en":"I had never experienced such excitement.\n\nNever had I experienced ...","correct_answer":"such excitement.","example":"NEVER","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B3_I1', 'ingles-b2-U21', 7, 'multiple_choice', 'What does it mean to ''kick the bucket''?', '{"prompt_es":"What does it mean to ''kick the bucket''?","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''kick the bucket'' es un eufemismo para morir.","stimulus_en":"","options":[{"id":"o1","text":"To celebrate"},{"id":"o2","text":"To die"},{"id":"o3","text":"To move quickly"},{"id":"o4","text":"To start something new"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B3_I2', 'ingles-b2-U21', 8, 'multiple_choice', 'If someone is ''under the weather'', how do they feel?', '{"prompt_es":"If someone is ''under the weather'', how do they feel?","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''under the weather'' se usa para describir a alguien que no se siente bien o está enfermo.","stimulus_en":"","options":[{"id":"o1","text":"Happy"},{"id":"o2","text":"Sad"},{"id":"o3","text":"Sick"},{"id":"o4","text":"Energetic"}],"correct_answer":"o3","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B3_I3', 'ingles-b2-U21', 9, 'multiple_choice', 'What does it mean if you ''hit the nail on the head''?', '{"prompt_es":"What does it mean if you ''hit the nail on the head''?","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión significa que alguien ha dado en el clavo, o ha hecho un comentario exacto.","stimulus_en":"","options":[{"id":"o1","text":"To make a mistake"},{"id":"o2","text":"To be incorrect"},{"id":"o3","text":"To describe something accurately"},{"id":"o4","text":"To work hard"}],"correct_answer":"o3","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B3_I4', 'ingles-b2-U21', 10, 'multiple_choice', 'If you ''spill the beans'', what have you done?', '{"prompt_es":"If you ''spill the beans'', what have you done?","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión ''spill the beans'' significa revelar un secreto o información confidencial.","stimulus_en":"","options":[{"id":"o1","text":"Kept a secret"},{"id":"o2","text":"Told a secret"},{"id":"o3","text":"Cooked food"},{"id":"o4","text":"Made a mistake"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
