INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U21_B3_I5', 'ingles-b2-U21', 11, 'multiple_choice', 'What does it mean to ''burn the midnight oil''?', '{"prompt_es":"What does it mean to ''burn the midnight oil''?","mastery_tag":"vocabulary:b2-idiomatic","explanation":"La expresión se refiere a trabajar o estudiar hasta muy tarde.","stimulus_en":"","options":[{"id":"o1","text":"To relax"},{"id":"o2","text":"To work late into the night"},{"id":"o3","text":"To waste time"},{"id":"o4","text":"To have a party"}],"correct_answer":"o2","complexity":3,"concept_tags":["vocabulary","idiomatic","unit","grammar"]}')
