INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U23_B3_I5', 'ingles-b2-U23', 11, 'multiple_choice', 'Choose the correct answer for each mixed conditional sentence.', '{"prompt_es":"Choose the correct answer for each mixed conditional sentence.","mastery_tag":"grammar:b2-mixed-conditionals","explanation":"Esta oración describe una situación hipotética en el presente que implica una acción futura.","stimulus_en":"If I knew his address, I ___ him a letter.","options":[{"id":"o1","text":"will send"},{"id":"o2","text":"would send"},{"id":"o3","text":"would have sent"},{"id":"o4","text":"have sent"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","mixed","conditionals","unit"]}')
