INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B2_I1', 'ingles-b2-U24', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''wish'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''wish'':","mastery_tag":"grammar:b2-wish-regret","explanation":"En esta frase, expresamos un arrepentimiento por no haber estudiado más, usando ''wish'' para referirnos a algo en el pasado que deseamos que fuera diferente.","stimulus_en":"I regret not studying harder for the exam.\n\nI wish I had studied harder for the exam. ...","correct_answer":"I wish I had studied harder for the exam.","example":"wish","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B2_I2', 'ingles-b2-U24', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''regret'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''regret'':","mastery_tag":"grammar:b2-wish-regret","explanation":"Aquí expresamos el arrepentimiento por no haber aceptado una oferta de trabajo, utilizando ''regret'' para enfatizar el sentimiento.","stimulus_en":"If only I had taken that job offer last year.\n\nI regret not taking that job offer last year. ...","correct_answer":"I regret not taking that job offer last year.","example":"regret","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B2_I3', 'ingles-b2-U24', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''wish'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''wish'':","mastery_tag":"grammar:b2-wish-regret","explanation":"En este caso, estamos utilizando ''wish'' para expresar un deseo sobre habilidades actuales, específicamente, un deseo sobre la fluidez en el idioma.","stimulus_en":"I wish I could speak French fluently.\n\nI wish I were able to speak French fluently. ...","correct_answer":"I wish I were able to speak French fluently.","example":"wish","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B2_I4', 'ingles-b2-U24', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''regret'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''regret'':","mastery_tag":"grammar:b2-wish-regret","explanation":"Usamos ''regret'' nuevamente para expresar el arrepentimiento por una decisión de inversión que no se tomó en el pasado.","stimulus_en":"If only I had invested in that project earlier.\n\nI regret not investing in that project earlier. ...","correct_answer":"I regret not investing in that project earlier.","example":"regret","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B2_I5', 'ingles-b2-U24', 6, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''wish'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''wish'':","mastery_tag":"grammar:b2-wish-regret","explanation":"En esta oración, usamos ''wish'' para indicar que deseamos que la planificación de la presentación hubiera sido mejor.","stimulus_en":"I wish we had planned the presentation better.\n\nI wish we had made better plans for the presentation. ...","correct_answer":"I wish we had made better plans for the presentation.","example":"wish","complexity":3,"concept_tags":["grammar","wish","regret","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B3_I1', 'ingles-b2-U24', 7, 'multiple_choice', 'Choose the correct answer for each question about inversion in sentences.', '{"prompt_es":"Choose the correct answer for each question about inversion in sentences.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se utiliza para enfatizar ''rarely''. En inglés, después de un adverbio como ''rarely'', se usa el verbo auxiliar ''do''.","stimulus_en":"Rarely ___ we see such talent.","options":[{"id":"o1","text":"do"},{"id":"o2","text":"does"},{"id":"o3","text":"are"},{"id":"o4","text":"is"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B3_I2', 'ingles-b2-U24', 8, 'multiple_choice', 'Choose the correct answer for each question about inversion in sentences.', '{"prompt_es":"Choose the correct answer for each question about inversion in sentences.","mastery_tag":"grammar:b2-inversion","explanation":"Al igual que ''rarely'', ''seldom'' también requiere el uso de ''do'' para la inversión.","stimulus_en":"Seldom ___ I forget my keys.","options":[{"id":"o1","text":"do"},{"id":"o2","text":"does"},{"id":"o3","text":"am"},{"id":"o4","text":"is"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B3_I3', 'ingles-b2-U24', 9, 'multiple_choice', 'Choose the correct answer for each question about inversion in sentences.', '{"prompt_es":"Choose the correct answer for each question about inversion in sentences.","mastery_tag":"grammar:b2-inversion","explanation":"Con ''never'', usamos ''has'' para formar la inversión en presente perfecto.","stimulus_en":"Never ___ he been to Europe.","options":[{"id":"o1","text":"has"},{"id":"o2","text":"have"},{"id":"o3","text":"is"},{"id":"o4","text":"does"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
