INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B3_I4', 'ingles-b2-U24', 10, 'multiple_choice', 'Choose the correct answer for each question about inversion in sentences.', '{"prompt_es":"Choose the correct answer for each question about inversion in sentences.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se usa en frases con ''not only... but also''. Aquí, usamos ''is'' para ''she''.","stimulus_en":"Not only ___ she a doctor, but also a writer.","options":[{"id":"o1","text":"is"},{"id":"o2","text":"was"},{"id":"o3","text":"does"},{"id":"o4","text":"has"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
