INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U24_B3_I5', 'ingles-b2-U24', 11, 'multiple_choice', 'Choose the correct answer for each question about inversion in sentences.', '{"prompt_es":"Choose the correct answer for each question about inversion in sentences.","mastery_tag":"grammar:b2-inversion","explanation":"Con ''under no circumstances'', se usa ''should'' para enfatizar la prohibición.","stimulus_en":"Under no circumstances ___ you leave the room.","options":[{"id":"o1","text":"should"},{"id":"o2","text":"would"},{"id":"o3","text":"could"},{"id":"o4","text":"might"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
