INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B1_I1', 'ingles-b2-U26', 1, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"En inglés, cuando usamos ''seldom'' al inicio de una oración, se requiere inversión de sujeto y verbo, formando ''Seldom do we...''.","stimulus_en":"Seldom ___ we see such talented individuals in our field.","options":[{"id":"o1","text":"do"},{"id":"o2","text":"does"},{"id":"o3","text":"are"},{"id":"o4","text":"is"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B1_I2', 'ingles-b2-U26', 2, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se utiliza aquí porque ''rarely'' introduce la oración, requiriendo que usemos ''do'' para la forma correcta.","stimulus_en":"Rarely ___ we receive feedback that is this constructive.","options":[{"id":"o1","text":"have"},{"id":"o2","text":"do"},{"id":"o3","text":"are"},{"id":"o4","text":"has"}],"correct_answer":"o2","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B1_I3', 'ingles-b2-U26', 3, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"En la frase con ''never before'', usamos ''had'' para indicar que es una experiencia pasada que no ha ocurrido antes.","stimulus_en":"Never before ___ I encountered such a challenging project.","options":[{"id":"o1","text":"had"},{"id":"o2","text":"have"},{"id":"o3","text":"did"},{"id":"o4","text":"does"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B1_I4', 'ingles-b2-U26', 4, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La estructura ''Only after'' causa que invirtamos el sujeto y el verbo, aquí ''did'' se usa correctamente.","stimulus_en":"Only after the meeting ___ we understand the new strategy.","options":[{"id":"o1","text":"did"},{"id":"o2","text":"have"},{"id":"o3","text":"do"},{"id":"o4","text":"are"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B1_I5', 'ingles-b2-U26', 5, 'multiple_choice', 'Choose the correct option to complete each sentence using inversion.', '{"prompt_es":"Choose the correct option to complete each sentence using inversion.","mastery_tag":"grammar:b2-inversion","explanation":"La inversión se aplica después de ''hardly'' y debemos usar ''was'' para mantener la concordancia temporal de la oración.","stimulus_en":"Hardly ___ the report finalized when we received new data.","options":[{"id":"o1","text":"was"},{"id":"o2","text":"is"},{"id":"o3","text":"were"},{"id":"o4","text":"has"}],"correct_answer":"o1","complexity":3,"concept_tags":["grammar","inversion","unit"]}')
