INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B2_I1', 'ingles-b2-U26', 6, 'multiple_choice', 'What is the main purpose of a lecture?', '{"prompt_es":"What is the main purpose of a lecture?","mastery_tag":"listening:b2-lectures","explanation":"El objetivo principal de una conferencia es compartir información con la audiencia sobre un tema específico.","stimulus_en":"","options":[{"id":"o1","text":"To entertain the audience"},{"id":"o2","text":"To share information"},{"id":"o3","text":"To sell products"},{"id":"o4","text":"To conduct interviews"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B2_I2', 'ingles-b2-U26', 7, 'multiple_choice', 'Which of the following is a key feature of effective presentations?', '{"prompt_es":"Which of the following is a key feature of effective presentations?","mastery_tag":"listening:b2-lectures","explanation":"Las ayudas visuales son fundamentales en presentaciones efectivas, ya que ayudan a captar la atención y a entender mejor el contenido.","stimulus_en":"","options":[{"id":"o1","text":"Long and complex sentences"},{"id":"o2","text":"Visual aids"},{"id":"o3","text":"Silent moments"},{"id":"o4","text":"No interaction"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
