INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B2_I3', 'ingles-b2-U26', 8, 'multiple_choice', 'How should a speaker engage their audience during a lecture?', '{"prompt_es":"How should a speaker engage their audience during a lecture?","mastery_tag":"listening:b2-lectures","explanation":"Hacer preguntas retóricas puede involucrar a la audiencia y fomentar la reflexión sobre el tema presentado.","stimulus_en":"","options":[{"id":"o1","text":"By reading directly from notes"},{"id":"o2","text":"By asking rhetorical questions"},{"id":"o3","text":"By avoiding eye contact"},{"id":"o4","text":"By speaking monotonously"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
