INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B2_I4', 'ingles-b2-U26', 9, 'multiple_choice', 'What is often used to summarize a lecture at the end?', '{"prompt_es":"What is often used to summarize a lecture at the end?","mastery_tag":"listening:b2-lectures","explanation":"Una buena conclusión es esencial para resumir los puntos clave y dejar una impresión duradera en la audiencia.","stimulus_en":"","options":[{"id":"o1","text":"An introduction"},{"id":"o2","text":"A conclusion"},{"id":"o3","text":"More questions"},{"id":"o4","text":"Visual distractions"}],"correct_answer":"o2","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
