INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('B2_U26_B2_I5', 'ingles-b2-U26', 10, 'multiple_choice', 'Why is it important to know the audience''s background before a presentation?', '{"prompt_es":"Why is it important to know the audience''s background before a presentation?","mastery_tag":"listening:b2-lectures","explanation":"Conocer el contexto de la audiencia ayuda a adaptar el contenido para que sea comprensible y relevante para ellos.","stimulus_en":"","options":[{"id":"o1","text":"To adjust the length of the presentation"},{"id":"o2","text":"To make it more entertaining"},{"id":"o3","text":"To tailor the content to their knowledge"},{"id":"o4","text":"To use complicated vocabulary"}],"correct_answer":"o3","complexity":3,"concept_tags":["listening","lectures","unit","grammar"]}')
